	/**
	 * top Solutions
	 * @author shenpengyan
	 *
	 */
	class Solution{
		public int lengthOfLongestSubstring(String s) {
			int i = 0, j = 0, max = 0;
		    Set<Character> set = new HashSet<>();
		    
		    while (j < s.length()) {
		        if (!set.contains(s.charAt(j))) {
		            set.add(s.charAt(j++));
		            max = Math.max(max, set.size());
		        } else {
		            set.remove(s.charAt(i++));
		        }
		    }
		    
		    return max;
		}
	}
	/**
	 * 最后一个测试集超时
	 * 
	 * 思路比较对，但是substring比较耗时，用一个set保存就好了
	 * @author shenpengyan
	 *
	 */
	class SolutionTLE {
		public int lengthOfLongestSubstring(String s) {
			if (s == null || s.length() == 0) {
				return 0;
			}
			int maxLength = 1;
			int[] maxContinueDiffs = new int[s.length()];
			maxContinueDiffs[0] = 1;
			for (int i = 1; i < s.length(); i++) {
				int lastIndexOfthisChar = s.substring(0, i).lastIndexOf("" + s.charAt(i));
				maxContinueDiffs[i] = (lastIndexOfthisChar >= i - maxContinueDiffs[i - 1]) ? i - lastIndexOfthisChar
						: maxContinueDiffs[i - 1] + 1;
				maxLength = maxContinueDiffs[i] > maxLength ? maxContinueDiffs[i] : maxLength;
			}
			return maxLength;
		}
	}

